package classroom_tutorials;

public class String_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "John is from united states";
		System.out.println(s.toUpperCase()
				
				);
		System.out.println("Number of characters "+s.length());
		System.out.println("character in index " +s.charAt((s.length())-1));
	
		System.out.println("Number of characters "+s.charAt(5));
	}

}
